mapper.select:
select t1.id, t1.title, t1.score, t2.type qtype,t3.realname teacherName from question_judge t1 left join question_type t2 on t1.fk_qtype = t2.id left join teacher t3 on t1.fk_teacher = t3.id where t1.del_flag = 0
and t1.title like #{title}
and t1.fk_qtype = #{qtype}
order by t1.id desc

select

from question_judge
where title = #{title} and id <> #{id}

